mapper.select:
select
from t_bill
del_flag = '0' AND account = #{account,jdbcType=VARCHAR}
AND updated_at > DATE_FORMAT(#{updatedAt,jdbcType=VARCHAR}, '%y-%m-%d %H:%i:%s')
ORDER BY updated_at DESC
LIMIT #{start,jdbcType=INTEGER}, #{pageCount,jdbcType=INTEGER}

select COUNT(*) from t_bill
del_flag = '0' AND account = #{account,jdbcType=VARCHAR}
AND updated_at > DATE_FORMAT(#{updatedAt,jdbcType=VARCHAR}, '%y-%m-%d %H:%i:%s')
